window.addEventListener("scroll",(function(){var t=document.querySelector(".about__button");window.pageYOffset>500?(t.style.opacity="1",t.style.pointerEvents="all"):(t.style.opacity="0",t.style.pointerEvents="none")}));var anchors=document.querySelectorAll('a[href*="#"]');anchors.forEach((function(t){t.addEventListener("click",(function(e){e.preventDefault();var n=t.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})}))}));